class Queue{constructor(pace=300,queue=[]){this.pace=pace,this.queue=queue,this.timer=null}next(item){this.timer=setTimeout(()=>{this.timer=null,item.fn(),this.queue.length&&this.next(this.queue.shift())},item.delay)}add(fn,delay=this.pace){this.queue.push({fn:fn,delay:delay})}start(){this.queue.length&&this.next(this.queue.shift())}stop(){clearTimeout(this.timer)}reset(){this.stop(),this.queue=[]}}export default Queue;